import React from 'react';
import { Container, Row, Col, Button, Accordion, Card } from 'react-bootstrap';

const Sidebar = () => {
  return (
    <Col md={3} className="sidebar mt-5">
      <div id="mySidenav" className="sidenav">
        <Accordion>
          {/* Category 1 */}
          <Card>
            <Card.Header className="header_mobile" id="heading-1">
              <Accordion.Toggle as={Button} variant="link" eventKey="collapse-1">
                <span className="float-right m-2"><i className="fas fa-chevron-down"></i></span>
                <a>বিষয় </a>
              </Accordion.Toggle>
            </Card.Header>
            <Accordion.Collapse eventKey="collapse-1">
              <Card.Body>
                <ul className="mob_menu_max_height">
                  <li className="mobile_menu"><a href="0#">বেস্ট সেলার</a></li>
                  <li className="mobile_menu"><a href="0#">বেস্ট সেলার</a></li>
                  {/* Add more menu items as needed */}
                </ul>
              </Card.Body>
            </Accordion.Collapse>
          </Card>

          {/* Category 2 */}
          <Card>
            <Card.Header className="header_mobile" id="heading-2">
              <Accordion.Toggle as={Button} variant="link" eventKey="collapse-2">
                <span className="float-right m-2"><i className="fas fa-chevron-down"></i></span>
                <a>লেখক </a>
              </Accordion.Toggle>
            </Card.Header>
            <Accordion.Collapse eventKey="collapse-2">
              <Card.Body>
                <ul className="mob_menu_max_height">
                  <li className="mobile_menu"><a href="0#">হুমায়ূন আহমেদ</a></li>
                  <li className="mobile_menu"><a href="0#">শাহরিয়ার খান</a></li>
                  {/* Add more menu items as needed */}
                </ul>
              </Card.Body>
            </Accordion.Collapse>
          </Card>

          {/* Add more accordion cards for other sections (প্রকাশনী, একুশে বইমেলা, সর্বোচ্চ ডিস্কাউন্ট, স্টেশনারি) here */}
        </Accordion>
      </div>
    </Col>
  );
};

const HeroSection = () => {
  return (
    <Container fluid className="col-md-9 mt-5 m-0 p-0">
      <div id="hero-section" className="carousel slide" data-ride="carousel">
        {/* Carousel Indicators */}
        <ol className="carousel-indicators">
          <li data-target="#hero-section" data-slide-to="0" className="active"></li>
          <li data-target="#hero-section" data-slide-to="1"></li>
          <li data-target="#hero-section" data-slide-to="2"></li>
        </ol>

        {/* Carousel Inner */}
        <div className="carousel-inner">
          <div className="carousel-item active">
            <img src="./src/pbs/hero/1.png" className="d-block w-100" alt="Slide 1" />
          </div>
          <div className="carousel-item">
            <img src="./src/pbs/hero/2.png" className="d-block w-100" alt="Slide 2" />
          </div>
          {/* Add more carousel items as needed */}
        </div>

        {/* Carousel Controls */}
        <a className="carousel-control-prev" href="#hero-section" role="button" data-slide="prev">
          <span className="carousel-control-prev-icon" aria-hidden="true"></span>
          <span className="sr-only">Previous</span>
        </a>
        <a className="carousel-control-next" href="#hero-section" role="button" data-slide="next">
          <span className="carousel-control-next-icon" aria-hidden="true"></span>
          <span className="sr-only">Next</span>
        </a>
      </div>
    </Container>
  );
};

const Component_content = () => {
  return (
    <Container fluid>
      <Row>
        <Sidebar />
        <HeroSection />
      </Row>
    </Container>
  );
};

export default Component_content;
