// Footer.js
import React from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Footer.css'; // You can create a separate CSS file for styling

const Footer = () => {
  return (
    <footer>
      <Container fluid className="p-3">
        <Row>
          {/* Contact Information */}
          <Col lg={3} className="ml-auto">
            {/* Contact Items */}
            {/* ... (Same as provided in the HTML) */}
          </Col>

          {/* Page Links */}
          <Col md={3} className="border-end">
            {/* Footer Column */}
            {/* ... (Same as provided in the HTML) */}
          </Col>

          {/* Help Links */}
          <Col md={3} className="border-end">
            {/* Footer Column */}
            {/* ... (Same as provided in the HTML) */}
          </Col>

          {/* Facebook Link */}
          <Col md={3} className="border-end">
            {/* Footer Column */}
            {/* ... (Same as provided in the HTML) */}
          </Col>
        </Row>

        {/* Social Icons */}
        <div className="social">
          {/* ... (Same as provided in the HTML) */}
        </div>

        {/* Payment Image */}
        <div className="footer-img bg-white p-3">
          {/* ... (Same as provided in the HTML) */}
        </div>

        {/* Copyright */}
        <div className="footer-copy p-3">
          <p className="text-center">&copy; 2023 pbs.com.bd All rights Reserved.</p>
        </div>
      </Container>
    </footer>
  );
};

export default Footer;
