import React from 'react';
import { Accordion, Card, Button } from 'react-bootstrap';

const Sidebar = () => {
  const closeNav = () => {
    // Your JavaScript code goes here

// Example: Change content based on device type
window.addEventListener('load', () => {
    const screenWidth = window.innerWidth;

    if (screenWidth <= 600) {
        // Code for mobile devices
        console.log('Mobile device detected.');
    } else if (screenWidth > 600 && screenWidth <= 768) {
        // Code for tablet devices
        console.log('Tablet device detected.');
    } else {
        // Code for desktop and laptop devices
        console.log('Desktop or laptop device detected.');
    }
});

  };

  return (
    <div id="mySidenav" className="sidenav">
      <Accordion>
        {/* Category 1: বিষয় */}
        <Card>
          <Card.Header>
            <Accordion.Toggle as={Button} variant="link" eventKey="1">
              বিষয়
            </Accordion.Toggle>
          </Card.Header>
          <Accordion.Collapse eventKey="1">
            <Card.Body>
              <ul>
                <li className="mobile_menu"><a href="0#">বেস্ট সেলার</a></li>
                <li className="mobile_menu"><a href="0#">বেস্ট সেলার</a></li>
                {/* Add more menu items as needed */}
              </ul>
            </Card.Body>
          </Accordion.Collapse>
        </Card>

        {/* Category 2: লেখক */}
        <Card>
          <Card.Header>
            <Accordion.Toggle as={Button} variant="link" eventKey="2">
              লেখক
            </Accordion.Toggle>
          </Card.Header>
          <Accordion.Collapse eventKey="2">
            <Card.Body>
              <ul>
                {/* Add authors as needed */}
              </ul>
            </Card.Body>
          </Accordion.Collapse>
        </Card>

        {/* Add more cards for other sections (প্রকাশনী, একুশে বইমেলা, সর্বোচ্চ ডিসকাউন্ট, স্টেশনারি) here */}
      </Accordion>
    </div>
  );
};

export default Sidebar;
